sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },
	checkWhenNeeded: function(newValue){

	var rightNow = new GlideDateTime();
	var whenNeeded = new GlideDateTime(newValue);
	if(whenNeeded.before(rightNow)){
		gs.addErrorMessage("The time selected cannot be in the past");
		return false;
	}

	var dateWhen = whenNeeded.getLocalDate();	
	var dateNow = rightNow.getLocalDate();
	if(dateWhen.compareTo(dateNow) == 0){
		gs.addErrorMessage("The time selected cannot be of the same day");
		return false;
	}
		return true;
	},
    type: 'NeedItUtils'
};
